const BusinessHours = () => {
  const hours = [{ day: 'Sunday', hour: '10:00 AM - 4:00 PM' }];
  return (
    <div
      className="mx-auto py-20 bg-white grid place-items-center px-6 lt:px-20 lg:px-32 xl:px-20"
      style={{ maxWidth: '1550px' }}>
      <div>
        <h3 className="uppercase text-center tracking-wider text-2xl xl:text-3xl font-semibold pb-3 border-solid">
          business hours
        </h3>
        <div className="w-52 xl:w-80 h-1 bg-black mb-10" style={{ backgroundColor: '#CCA43A' }} />
      </div>
      {hours.map((item, index) => (
        <div className="flex justify-between w-full border-b" key={index}>
          <div className="font-semibold text-md lg:text-lg text-right">
            <p>{item.day}</p>
          </div>
          <div className="font-semibold text-md lg:text-lg">
            <p>{item.hour}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default BusinessHours;
