import Marquee from 'react-fast-marquee';
import './Reviews.css';
import quotes from '../../../assets/quotes.png';
import { reviews } from '../../../data';

const Reviews = () => {
  return (
    <div className="pt-20 bg-white">
      <div
        className="relative mx-auto grid place-items-center px-6 xl:px-20"
        style={{ maxWidth: '1550px' }}>
        <p className="uppercase tracking-wider text-center text-2xl xl:text-3xl font-semibold pb-3">
          10+ years in business
        </p>
        <img
          src={quotes}
          alt=""
          className="w-9 absolute top-[57%] right-[58.5%] -translate-x-0 translate-y-[-50%]"
        />
        <p className="pt-6 text-2xl font-light tracking-wider">
          See what our clients <br /> have to say about us!
        </p>
      </div>
      <div className="mt-10 mx-auto" style={{ maxWidth: '1550px' }}>
        <Marquee className="marquee flex" direction="left" speed={50}>
          {reviews.map((review, index) => (
            <div
              key={index}
              className="marquee-content mx-4 rounded-2xl px-10 py-6"
              style={{ maxWidth: '400px', height: '175px', backgroundColor: '#F3EFEB' }}>
              <img src="" alt="" />
              <div className="flex">
                {[1, 2, 3, 4, 5].map((item, index) => (
                  <div key={index}>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 24 24"
                      width="16"
                      height="16">
                      <path
                        d="M12.0008 17L6.12295 20.5902L7.72105 13.8906L2.49023 9.40983L9.35577 8.85942L12.0008 2.5L14.6458 8.85942L21.5114 9.40983L16.2806 13.8906L17.8787 20.5902L12.0008 17Z"
                        fill="rgba(170,126,9,1)"></path>
                    </svg>
                  </div>
                ))}
              </div>
              <p>
                {review.review.slice(0, 125)} {review.review.length > 125 ? '...' : ''}
              </p>
              {review.review.length > 125 && <p className="text-venter text-sm">See full review</p>}
            </div>
          ))}
        </Marquee>
      </div>
    </div>
  );
};
export default Reviews;
