const slideStyles = {
  width: '100%',
  height: '100%',
  borderRadius: '10px',
  backgroundSize: 'cover',
  backgroundPosition: 'center'
};

const dotStyle = {
  margin: '0 3px',
  cursor: 'pointer',
  fontSize: '20px'
};
const Gallery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const slides = [
    { url: 'http://localhost:3000/image-1.jpg', title: 'beach' },
    { url: 'http://localhost:3000/image-2.jpg', title: 'boat' },
    { url: 'http://localhost:3000/image-3.jpg', title: 'forest' },
    { url: 'http://localhost:3000/image-4.jpg', title: 'city' },
    { url: 'http://localhost:3000/image-5.jpg', title: 'italy' }
  ];
  const goToPrevious = () => {
    const isFirstSlide = currentIndex === 0;
    const newIndex = isFirstSlide ? slides.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };
  const goToNext = () => {
    const isLastSlide = currentIndex === slides.length - 1;
    const newIndex = isLastSlide ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };
  const goToSlide = (slideIndex: number) => {
    setCurrentIndex(slideIndex);
  };
  const slideStylesWidthBackground = {
    ...slideStyles,
    backgroundImage: `url(${slides[currentIndex].url})`
  };

  return (
    <div className="relative h-full">
      <div>
        <div
          onClick={goToPrevious}
          className="absolute text-white cursor-pointer z-1"
          style={{ top: '50%', left: '32px', transform: 'translate(0, -50%)', fontSize: '45px' }}>
          ❰
        </div>
        <div
          onClick={goToNext}
          className="absolute text-white cursor-pointer z-1"
          style={{ top: '50%', right: '32px', transform: 'translate(0, -50%)', fontSize: '45px' }}>
          ❱
        </div>
      </div>
      <div style={slideStylesWidthBackground}></div>
      <div className="flex justify-center">
        {slides.map((slide, slideIndex) => (
          <div style={dotStyle} key={slideIndex} onClick={() => goToSlide(slideIndex)}>
            ●
          </div>
        ))}
      </div>
    </div>
  );
};

export default Gallery;
