import React, { useState } from 'react';
import './Slider.css';
import BtnSlider from './BtnSlider';
import image1 from '../../../assets/unnamed-1.jpg';
import image2 from '../../../assets/unnamed-2.jpg';
import image3 from '../../../assets/unnamed.jpg';
import image4 from '../../../assets/unnamed-7.jpg';
import image5 from '../../../assets/unnamed-9.jpg';
import leftArrow from '../../../assets/arrow-left.png';
import rightArrow from '../../../assets/arrow-right.png';
// note: move the image files to the dataSlider file
export default function Slider() {
  const [slideIndex, setSlideIndex] = useState(1);
  const images = [
    {
      id: 1,
      url: image1
    },
    {
      id: 2,
      url: image2
    },
    {
      id: 3,
      url: image3
    },
    {
      id: 4,
      url: image4
    },
    {
      id: 5,
      url: image5
    }
  ];

  const nextSlide = () => {
    if (slideIndex !== images.length) {
      setSlideIndex(slideIndex + 1);
    } else if (slideIndex === images.length) {
      setSlideIndex(1);
    }
  };

  const prevSlide = () => {
    if (slideIndex !== 1) {
      setSlideIndex(slideIndex - 1);
    } else if (slideIndex === 1) {
      setSlideIndex(images.length);
    }
  };

  const moveDot = (index: number) => {
    setSlideIndex(index);
  };

  return (
    <div className="pb-20 relative bg-white">
      <div className="grid place-items-center">
        <h3 className="mt-10 text-center text-2xl xl:text-4xl font-semibold pb-3 border-solid">
          GALLERY
        </h3>
        <div className="w-52 xl:w-64 h-1 bg-black mb-10" style={{ backgroundColor: '#CCA43A' }} />
      </div>
      <button onClick={prevSlide} className="btn-slide prev">
        <img src={leftArrow} />
      </button>
      <div className="container-slider overflow-hidden max-w-full">
        {images.map((image, index) => {
          return (
            <div
              key={image.id}
              className={slideIndex === index + 1 ? 'slide active-anim' : 'slide'}>
              <img src={image.url} className="w-full h-full" />
            </div>
          );
        })}
      </div>
      {/* <BtnSlider moveSlide={nextSlide} direction={'next'} />
      <BtnSlider moveSlide={prevSlide} direction={'prev'} /> */}

      <button onClick={nextSlide} className="btn-slide next">
        <img src={rightArrow} className="w-10 h-2" />
      </button>
      <div className="container-dots">
        {images.map((image, index) => (
          <div
            key={image.id}
            onClick={() => moveDot(index + 1)}
            className={slideIndex === index + 1 ? 'dot active' : 'dot'}></div>
        ))}
      </div>
    </div>
  );
}
