import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const ContactUs = () => {
  const [loading, setLoading] = useState(false);

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    message: ''
  });

  const [formError, setFormError] = useState({
    firstName: '',
    lastName: '',
    email: '',
    message: ''
  });

  const changeHandler = <T extends HTMLInputElement | HTMLTextAreaElement>(
    e: React.ChangeEvent<T>
  ) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const submitHandler = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);

    const errors = {
      firstName: '',
      lastName: '',
      email: '',
      message: ''
    };

    if (!formData.firstName) {
      errors.firstName = 'First name is required';
    }

    if (!formData.lastName) {
      errors.lastName = 'Last name is required';
    }

    if (
      !formData.email.includes('@') ||
      (!formData.email.includes('.com') && formData.email.length > 1)
    ) {
      errors.email = 'A valid email is required';
    }

    if (!formData.email) {
      errors.email = 'Email is required';
    }

    if (!formData.message) {
      errors.message = 'A message is required';
    }

    if (errors.email || errors.firstName || errors.lastName || errors.message) {
      setFormError(errors);
      setLoading(false);
      return;
    }

    setFormData({
      firstName: '',
      lastName: '',
      email: '',
      message: ''
    });
  };

  return (
    <div
      className="xl:flex px-6 md:p-20 mx-auto my-20 mx-20 rounded-3xl grid place-items-center"
      style={{ maxWidth: '1550px', backgroundColor: '#CCB6A0' }}>
      <div className="xl:w-1/2">
        <h1 className="text-2xl xl:text-3xl font-semibold">
          Welcome back to School of Bubberson,
          <br />
          Sign In to Continue.
        </h1>
        <p className="mt-4 mb-14">
          Don’t have an account?{' '}
          <Link to="/register">
            <span className="underline font-semibold">Create your account</span>
          </Link>
          <br />
          and start learning today!
        </p>
      </div>
      <div className="xl:w-1/2 text-sm">
        <form onSubmit={submitHandler}>
          <div className="mb-8">
            <label htmlFor="firstName" className="font-semibold">
              First Name
              <br />
              <input
                type="text"
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={changeHandler}
                className="font-normal py-4 px-5 mt-3 text-xs md:text-sm font-medium text-black w-full"
                style={{ backgroundColor: '#E8EEF8' }}
              />
            </label>
            <p className="text-xs text-red-600 mt-2">{formError.firstName}</p>
          </div>
          <div className="mb-8">
            <label htmlFor="lastNamelastName" className="font-semibold">
              Last Name
              <br />
              <input
                type="text"
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={changeHandler}
                className="font-normal py-4 px-5 mt-3 text-xs md:text-sm font-medium text-black w-full"
                style={{ backgroundColor: '#E8EEF8' }}
              />
            </label>
            <p className="text-xs text-red-600 mt-2">{formError.lastName}</p>
          </div>
          <div className="mb-8">
            <label htmlFor="email" className="font-semibold">
              Email
              <br />
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={changeHandler}
                placeholder="johnsmith@gmail.com"
                className="font-normal py-4 px-5 mt-3 text-xs md:text-sm font-medium text-black w-full"
                style={{ backgroundColor: '#E8EEF8' }}
              />
            </label>
            <p className="text-xs text-red-600 mt-2">{formError.email}</p>
          </div>
          <div className="mb-8">
            <label htmlFor="message" className="font-semibold">
              Message
              <br />
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={changeHandler}
                placeholder="What can we help you with today?"
                className="font-normal py-4 px-5 mt-3 text-xs md:text-sm font-medium text-black w-full"
                style={{ backgroundColor: '#E8EEF8' }}></textarea>
            </label>
            <p className="text-xs text-red-600 mt-2">{formError.message}</p>
          </div>
          <p className="text-xs text-red-600 mb-2">{''}</p>

          <div className="relative">
            <button
              type="submit"
              className="bg-yellow-200 py-4 px-5 text-xs md:text-sm font-medium text-white mb-4 w-full">
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ContactUs;
