import React, { useState } from 'react';
import Carousel from 'react-simply-carousel';
import image1 from '../../../assets/carousel-item-1.png';
import image2 from '../../../assets/11.png';
import image3 from '../../../assets/12.png';
import image4 from '../../../assets/13.png';
import image5 from '../../../assets/14.png';
import image6 from '../../../assets/15.png';

const ImageCarousel = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const images = [
    {
      id: 1,
      url: image1
    },
    {
      id: 2,
      url: image2
    },
    {
      id: 3,
      url: image3
    },
    {
      id: 4,
      url: image4
    },
    {
      id: 5,
      url: image5
    },
    {
      id: 6,
      url: image6
    }
  ];

  return (
    <div className="mx-auto grid place-items-center px-6 md:px-12" style={{ maxWidth: '1550px' }}>
      <Carousel
        containerProps={{
          style: {
            width: '100%',
            justifyContent: 'space-between',
            userSelect: 'none'
          }
        }}
        preventScrollOnSwipe
        swipeTreshold={60}
        activeSlideIndex={activeSlide}
        activeSlideProps={{
          style: {
            background: 'blue'
          }
        }}
        onRequestChange={setActiveSlide}
        forwardBtnProps={{
          children: '>',
          style: {
            width: 60,
            height: 60,
            minWidth: 60,
            alignSelf: 'center'
          }
        }}
        backwardBtnProps={{
          children: '<',
          style: {
            width: 60,
            height: 60,
            minWidth: 60,
            alignSelf: 'center',
            backgroundColor: 'red',
            borderRadius: '50%'
          }
        }}
        dotsNav={{
          show: true,
          itemBtnProps: {
            style: {
              height: 16,
              width: 16,
              borderRadius: '50%',
              border: 0
            }
          },
          activeItemBtnProps: {
            style: {
              height: 16,
              width: 16,
              borderRadius: '50%',
              border: 0,
              background: 'black'
            }
          }
        }}
        itemsToShow={3}
        speed={400}>
        {images.map((item, index) => (
          <div
            style={{
              background: 'yellow',
              backgroundImage: `url(${item.url})`,
              width: 500,
              height: 350,
              border: '30px solid white',
              textAlign: 'center',
              lineHeight: '240px',
              boxSizing: 'border-box'
            }}
            key={index}></div>
        ))}
      </Carousel>
    </div>
  );
};

export default ImageCarousel;
