import React from 'react';
import './home.css';
import { Link } from 'react-router-dom';
import flower from '../../assets/spa-flowers.png';
import brazilian from '../../assets/3.png';
import legs from '../../assets/1.png';
import body from '../../assets/2.png';

const Services = () => {
  const data = [
    {
      id: 0,
      title: 'Brazilian',
      url: brazilian,
      description:
        'Hair is removed from the pubic area using a sugar paste that is applied to the skin in the opposite direction of hair growth.'
    },
    {
      id: 1,
      title: 'Legs',
      url: body,
      description:
        'Leg sugaring is a popular choice for those who want to remove hair from larger areas of the body in a safe and natural way.'
    },
    {
      id: 2,
      title: 'bikini',
      url: legs,
      description:
        'Bikini sugaring service is a natural and gentle hair removal treatment that targets the area outside of the panty line, leaving you with a smooth and hair-free bikini area.      '
    },
    {
      id: 3,
      title: 'Facial',
      url: brazilian,
      description:
        'Hair is removed from the pubic area using a sugar paste that is applied to the skin in the opposite direction of hair growth and removed in the direction of hair growth.'
    }
  ];
  return (
    <section className="bg-white justify-center items-center my-24 px-6 md:px-12 text-sm md:text-base lg:text-lg">
      <div className="mx-auto" style={{ maxWidth: '1200px' }}>
        <h2 className="mb-40 mt-20 text-center text-4xl font-semibold leading-snug">
          Ditch the Wax and Embrace the Sugar! Our services offer a natural alternative for hair
          removal.
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-x-12 gap-y-32 flex flex-col">
          {data.map((service) => (
            <Link to="/services" key={service.id} className="mt-auto">
              <div className="container shadow" style={{ borderColor: '#605651' }}>
                <div>
                  <img src={service.url} alt="" />
                </div>
                <div className="px-4 py-8">
                  <h3
                    className="text-md xsl:text-xl sm:text-2xl md:text-3xl font-semibold uppercase"
                    style={{ color: '#CCA43A' }}>
                    {service.title}
                  </h3>
                  <p className="text-sm md:text-base lg:text-lg mt-2 mb-6 xs:mb-0">
                    {service.description}
                  </p>
                  <button
                    className="mt-10 border border-solid text-white px-4 py-1"
                    style={{ backgroundColor: '#CCA43A' }}>
                    View all services
                  </button>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;

