import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import './App.css';
import AnnouncementBanner from './components/Home/AnnouncementBanner';
import Navigation from './components/Navigation';
import Home from './components/Home';
import Services from './components/Services';
import Faq from './components/Faq';
import AboutUs from './components/AboutUs';
import Footer from './components/Footer';

function App() {
  const [showBanner, setShowBanner] = useState(false);
  const [closeBanner, setCloseBanner] = useState(false);
  const [isNavOpen, setIsNavOpen] = useState(false);
  const [isNavClosing, setIsNavClosing] = useState(false);

  useEffect(() => {
    window.addEventListener('scroll', scrollHandler);
    return () => window.removeEventListener('scroll', scrollHandler);
  }, [showBanner]);

  useEffect(() => {
    const bodyStyles = `
      body > *:not(#no-blur) {
        transition: filter 0.3s ease;
        filter: blur(3px);
      }
    `;

    const addStylesToBody = () => {
      const styleElement = document.createElement('style');
      styleElement.setAttribute('id', 'custom-styles');
      styleElement.innerHTML = bodyStyles;
      document.head.appendChild(styleElement);
    };

    const removeStylesFromBody = () => {
      const styleElement = document.getElementById('custom-styles');
      if (styleElement) {
        styleElement.parentNode.removeChild(styleElement);
      }
    };

    if (isNavOpen) {
      document.body.classList.add('no-scroll');
      addStylesToBody();
    } else {
      document.body.classList.remove('no-scroll');
      removeStylesFromBody();
    }

    return () => {
      removeStylesFromBody();
    };
  }, [isNavOpen]);

  const scrollHandler = () => {
    const scrollTop = window.pageYOffset;
    if (scrollTop >= 300 && !showBanner) {
      setShowBanner(true);
    } else if (scrollTop < 300 && showBanner) {
      setShowBanner(false);
    }
  };

  const closeBannerHandler = () => {
    setCloseBanner(true);
  };

  const handleCloseNav = () => {
    setIsNavClosing(true);
  };

  const handleAnimationEnd = (e: React.AnimationEvent<HTMLDivElement>) => {
    if (e.animationName === 'slideOut') {
      setIsNavOpen(false);
      setIsNavClosing(false);
      e.currentTarget.classList.remove('animate-slide-out');
    }
  };

  return (
    <div>
      <div className={` ${showBanner ? 'transition' : ''}`}>
        <AnnouncementBanner closeBannerHandler={closeBannerHandler} closeBanner={closeBanner} />
      </div>

      <Navigation
        closeBanner={closeBanner}
        setIsNavOpen={setIsNavOpen}
        isNavOpen={isNavOpen}
        isNavClosing={isNavClosing}
        handleCloseNav={handleCloseNav}
        handleAnimationEnd={handleAnimationEnd}
      />
      <div className="">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/services" element={<Services />} />
          <Route path="/faq" element={<Faq />} />
          <Route path="/about" element={<AboutUs />} />
        </Routes>
      </div>
      <Footer />
    </div>
  );
}

export default App;
