import React, { useState } from 'react';
import ReactSimplyCarousel from 'react-simply-carousel';
import image1 from '../../../assets/carousel-item-1.png';
import image2 from '../../../assets/11.png';
import image3 from '../../../assets/12.png';
import image4 from '../../../assets/13.png';
import image5 from '../../../assets/14.png';
import image6 from '../../../assets/15.png';

const ImageCarousel = () => {
  const [activeSlideIndex, setActiveSlideIndex] = useState(0);

  const images = [
    {
      id: 1,
      url: image1
    },
    {
      id: 2,
      url: image2
    },
    {
      id: 3,
      url: image3
    },
    {
      id: 4,
      url: image4
    },
    {
      id: 5,
      url: image5
    },
    {
      id: 6,
      url: image6
    }
  ];

  return (
    <div style={{ backgroundColor: '#F6F5F5' }}>
      <div
        className="mx-auto grid place-items-center  relative py-20"
        style={{ maxWidth: '1550px' }}>
        <ReactSimplyCarousel
          activeSlideIndex={activeSlideIndex}
          onRequestChange={setActiveSlideIndex}
          itemsToShow={1}
          itemsToScroll={1}
          forwardBtnProps={{
            //here you can also pass className, or any other button element attributes
            style: {
              alignSelf: 'center',
              background: '#CCA43A',
              border: 'none',
              borderRadius: '50%',
              color: 'white',
              cursor: 'pointer',
              fontSize: '20px',
              height: 50,
              lineHeight: 1,
              textAlign: 'center',
              width: 50,
              position: 'absolute',
              right: 215
            },
            children: <span>{`>`}</span>
          }}
          backwardBtnProps={{
            //here you can also pass className, or any other button element attributes
            style: {
              alignSelf: 'center',
              background: '#CCA43A',
              border: 'none',
              borderRadius: '50%',
              color: 'white',
              cursor: 'pointer',
              fontSize: '20px',
              height: 50,
              lineHeight: 1,
              textAlign: 'center',
              width: 50,
              position: 'absolute',
              left: 215
            },
            children: <span>{`<`}</span>
          }}
          dotsNav={{
            show: true,
            itemBtnProps: {
              style: {
                height: 8,
                width: 8,
                borderRadius: '50%',
                border: 0,
                background: '#E5E7EB',
                margin: '15px 2px 0 2px'
              }
            },
            activeItemBtnProps: {
              style: {
                height: 8,
                width: 8,
                borderRadius: '50%',
                border: 0,
                background: '#CCA43A',
                margin: '15px 2px 0 2px'
              }
            }
          }}
          responsiveProps={[
            {
              itemsToShow: 3,
              itemsToScroll: 1,
              minWidth: 768
            },
            {
              itemsToShow: 2,
              itemsToScroll: 1,
              minWidth: 350
            },
            {
              itemsToShow: 1,
              itemsToScroll: 1,
              minWidth: 0
            }
          ]}
          speed={400}
          easing="linear">
          {images.map((image, index) => (
            <div key={index} style={{ width: 500, height: 300, background: '#ff80ed' }}>
              <img src={image.url} alt="" />
            </div>
          ))}
        </ReactSimplyCarousel>
      </div>
    </div>
  );
};

export default ImageCarousel;
