import React, { useState } from 'react';
import ReactSimplyCarousel from 'react-simply-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import image1 from '../../../assets/carousel-item-1.png';
import image2 from '../../../assets/11.png';
import image3 from '../../../assets/12.png';
import image4 from '../../../assets/13.png';
import image5 from '../../../assets/14.png';
import image6 from '../../../assets/15.png';

const ImageCarousel = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const images = [
    {
      id: 1,
      url: image1
    },
    {
      id: 2,
      url: image2
    },
    {
      id: 3,
      url: image3
    },
    {
      id: 4,
      url: image4
    },
    {
      id: 5,
      url: image5
    },
    {
      id: 6,
      url: image6
    }
  ];

  return (
    <div className="mx-auto grid place-items-center px-6 md:px-12" style={{ maxWidth: '1550px' }}>
      <ReactSimplyCarousel
        activeSlideIndex={activeSlideIndex}
        onRequestChange={setActiveSlideIndex}
        itemsToShow={1}
        itemsToScroll={1}
        forwardBtnProps={{
          //here you can also pass className, or any other button element attributes
          style: {
            alignSelf: 'center',
            background: 'black',
            border: 'none',
            borderRadius: '50%',
            color: 'white',
            cursor: 'pointer',
            fontSize: '20px',
            height: 30,
            lineHeight: 1,
            textAlign: 'center',
            width: 30
          },
          children: <span>{`>`}</span>
        }}
        backwardBtnProps={{
          //here you can also pass className, or any other button element attributes
          style: {
            alignSelf: 'center',
            background: 'black',
            border: 'none',
            borderRadius: '50%',
            color: 'white',
            cursor: 'pointer',
            fontSize: '20px',
            height: 30,
            lineHeight: 1,
            textAlign: 'center',
            width: 30
          },
          children: <span>{`<`}</span>
        }}
        responsiveProps={[
          {
            itemsToShow: 2,
            itemsToScroll: 2,
            minWidth: 768
          }
        ]}
        speed={400}
        easing="linear">
        {/* here you can also pass any other element attributes. Also, you can use your custom components as slides */}
        <div style={{ width: 300, height: 300, background: '#ff80ed' }}>slide 0</div>
        <div style={{ width: 300, height: 300, background: '#065535' }}>slide 1</div>
        <div style={{ width: 300, height: 300, background: '#000000' }}>slide 2</div>
        <div style={{ width: 300, height: 300, background: '#133337' }}>slide 3</div>
        <div style={{ width: 300, height: 300, background: '#ffc0cb' }}>slide 4</div>
        <div style={{ width: 300, height: 300, background: '#ffffff' }}>slide 5</div>
        <div style={{ width: 300, height: 300, background: '#ffe4e1' }}>slide 6</div>
        <div style={{ width: 300, height: 300, background: '#008080' }}>slide 7</div>
        <div style={{ width: 300, height: 300, background: '#ff0000' }}>slide 8</div>
        <div style={{ width: 300, height: 300, background: '#e6e6fa' }}>slide 9</div>
      </ReactSimplyCarousel>
    </div>
  );
};

export default ImageCarousel;
