import React, { useState } from 'react';
import './Slider.css';
import BtnSlider from './BtnSlider';

export default function Slider() {
  const [slideIndex, setSlideIndex] = useState(1);
  const images = [
    {
      id: 1,
      url: 'https://www.google.com/images/rabbit'
    },
    {
      id: 2,
      url: 'https://www.google.com/images/turtle'
    },
    {
      id: 3,
      url: 'https://www.google.com/images/fish'
    },
    {
      id: 4,
      url: 'https://www.google.com/images/dog'
    },
    {
      id: 5,
      url: 'https://www.google.com/images/cat'
    }
  ];

  const nextSlide = () => {
    if (slideIndex !== images.length) {
      setSlideIndex(slideIndex + 1);
    } else if (slideIndex === images.length) {
      setSlideIndex(1);
    }
  };

  const prevSlide = () => {
    if (slideIndex !== 1) {
      setSlideIndex(slideIndex - 1);
    } else if (slideIndex === 1) {
      setSlideIndex(images.length);
    }
  };

  const moveDot = (index: number) => {
    setSlideIndex(index);
  };

  return (
    <div className="container-slider">
      {images.map((image, index) => {
        return (
          <div key={image.id} className={slideIndex === index + 1 ? 'slide active-anim' : 'slide'}>
            <img src={image.url} />
          </div>
        );
      })}
      <BtnSlider moveSlide={nextSlide} direction={'next'} />
      <BtnSlider moveSlide={prevSlide} direction={'prev'} />

      <div className="container-dots">
        {images.map((image, index) => (
          <div
            key={image.id}
            onClick={() => moveDot(index + 1)}
            className={slideIndex === index + 1 ? 'dot active' : 'dot'}></div>
        ))}
      </div>
    </div>
  );
}
