import React, { useState } from 'react';
import ReactSimplyCarousel from 'react-simply-carousel';
import image1 from '../../../assets/carousel-item-1.png';
import image2 from '../../../assets/11.png';
import image3 from '../../../assets/12.png';
import image4 from '../../../assets/13.png';
import image5 from '../../../assets/14.png';
import image6 from '../../../assets/15.png';

const ImageCarousel = () => {
  const [activeSlideIndex, setActiveSlideIndex] = useState(0);

  const images = [
    {
      id: 1,
      url: image1
    },
    {
      id: 2,
      url: image2
    },
    {
      id: 3,
      url: image3
    },
    {
      id: 4,
      url: image4
    },
    {
      id: 5,
      url: image5
    },
    {
      id: 6,
      url: image6
    }
  ];

  return (
    <div className="mx-auto grid place-items-center px-6 md:px-12" style={{ maxWidth: '1550px' }}>
      <ReactSimplyCarousel
        activeSlideIndex={activeSlideIndex}
        onRequestChange={setActiveSlideIndex}
        itemsToShow={1}
        itemsToScroll={1}
        forwardBtnProps={{
          //here you can also pass className, or any other button element attributes
          style: {
            alignSelf: 'center',
            background: 'black',
            border: 'none',
            borderRadius: '50%',
            color: 'white',
            cursor: 'pointer',
            fontSize: '20px',
            height: 30,
            lineHeight: 1,
            textAlign: 'center',
            width: 30
          },
          children: <span>{`>`}</span>
        }}
        backwardBtnProps={{
          //here you can also pass className, or any other button element attributes
          style: {
            alignSelf: 'center',
            background: 'black',
            border: 'none',
            borderRadius: '50%',
            color: 'white',
            cursor: 'pointer',
            fontSize: '20px',
            height: 30,
            lineHeight: 1,
            textAlign: 'center',
            width: 30
          },
          children: <span>{`<`}</span>
        }}
        dotsNav={{
          show: true,
          itemBtnProps: {
            style: {
              height: 8,
              width: 8,
              borderRadius: '50%',
              border: 0,
              background: 'red'
            }
          },
          activeItemBtnProps: {
            style: {
              height: 8,
              width: 8,
              borderRadius: '50%',
              border: 0,
              background: 'black'
            }
          }
        }}
        responsiveProps={[
          {
            itemsToShow: 3,
            itemsToScroll: 1,
            minWidth: 768
          }
        ]}
        speed={400}
        easing="linear">
        {images.map((image, index) => (
          <div key={index} style={{ width: 500, height: 300, background: '#ff80ed' }}>
            <img src={image.url} alt="" />
          </div>
        ))}
      </ReactSimplyCarousel>
    </div>
  );
};

export default ImageCarousel;
