import { useState } from 'react';

const ServiceTypes = () => {
  const [activeTab, setActiveTab] = useState('Sugar');
  const [showSugarTab, setShowSugarTab] = useState(true);
  const [showWaxTab, setShowWaxTab] = useState(false);
  const [showEyebrowsTab, setShowClasses] = useState(false);
  const [showAssignments, setShowAssignments] = useState(false);

  const handleTabSelections = (selected: string) => {
    if (selected === 'Sugar') {
      setShowSugarTab(true);
      setShowWaxTab(false);
      setShowClasses(false);
      setShowAssignments(false);
      setActiveTab('Sugar');
    }
    if (selected === 'Wax') {
      setShowSugarTab(false);
      setShowWaxTab(true);
      setShowClasses(false);
      setShowAssignments(false);
      setActiveTab('Wax');
    }
    if (selected === 'Eyebrows') {
      setShowSugarTab(false);
      setShowWaxTab(false);
      setShowClasses(true);
      setShowAssignments(false);
      setActiveTab('Eyebrows');
      // setSelectedClass(null);
    }
    if (selected === 'Eyelashes') {
      setShowSugarTab(false);
      setShowWaxTab(false);
      setShowClasses(false);
      setShowAssignments(true);
      setActiveTab('Eyelashes');
    }
  };

  return (
    <div className="mt-20">
      <ul className="grid grid-cols-4 gap-x-2 place-items-center">
        <li className="bg-blue-200 px-10 py-4">Sugar</li>
        <li>Wax</li>
        <li>Eyebrows</li>
        <li>Eyelashes</li>
      </ul>
    </div>
  );
};

export default ServiceTypes;
